import axios from 'axios';
import { useState, useEffect } from 'react'



const App = () => {

  const [persons, setPersons] = useState([]);
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");

  const notes = useEffect(() => {
    axios.get("http://localhost:3002/persons").
      then(response => {
        setPersons(response.data);
      })
  }, []);

  const handelNameChange = (event) => {
    setName(event.target.value);
  }

  const handelNumberChange = (event) => {
    setNumber(event.target.value);
  }

  const addNote = (event) => {
    event.preventDefault();
    const entry = {
      id: persons.length + 1,
      name: name,
      number: number
    }
    axios.post("http://localhost:3002/persons", entry).
      then(response => {
        setPersons(persons.concat(response.data));
        setName("");
        setNumber("");
      });
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <h3>add a new</h3>
      <form onSubmit={addNote}>
        Name: <input value={name} onChange={handelNameChange} required></input>
        Number: <input value={number} onChange={handelNumberChange} required></input>
        <button type="submit">save</button>
      </form>
      <h3>Numbers</h3>
      {persons.map(person => <p key={person.id}>{person.name} {person.number}</p>)}
    </div >
  )
}

export default App
