import axios from 'axios';
import { useState, useEffect } from 'react'



const App = () => {

  const [persons, setPersons] = useState([]);
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");
  const [person, setPerson] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3000/persons").
      then(response => {
        setPersons(response.data);
      })
  }, []);

  const handelNameChange = (event) => {
    setName(event.target.value);
  }

  const handelNumberChange = (event) => {
    setNumber(event.target.value);
  }

  const submit = (event) => {
    console.log(event.target.value);
    event.preventDefault();
    const entry = {
      id: persons.length + 1,
      name: name,
      number: number
    }
    axios.post("http://localhost:3000/persons", entry).
      then(response => { console.log(response); })
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <h3>add a new</h3>
      <form action={submit}>
        Name: <input value={name} onChange={handelNameChange} required></input>
        Number: <input value={number} onChange={handelNumberChange} required></input>
      </form>
      <h3>Numbers</h3>
      {persons.map(person => <p key={person.id}>{person.name} {person.number}</p>)}
    </div>
  )
}

export default App
