  return (
    <form onSubmit={addToPhoneBook}>
      <div>
        name: <input value={newName} onChange={handelNameChange} required />
      </div>
      <div>
        number: <input type="tel" value={number} onChange={handelNumberChange} required />
      </div>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  )
}


