import { useState } from 'react'
import Filter from './components/Filter';
import PersonForm from './components/PersonForms';
import Persons from './components/persons';


const App = () => {

  setTimeout(() => {
    console.log('loop..')
    let i = 0
    while (i < 50000000000) {
      i++
    }
    console.log('end')
  }, 5000)

  const [filteredPersons, setFilteredPersons] = useState([]);
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState("")
  const [number, setNumber] = useState("");

  const handelNameChange = (event) => {
    setNewName(event.target.value);
  }

  const handelNumberChange = (event) => {
    setNumber(event.target.value);
  }

  const addToPhoneBook = (event) => {
    event.preventDefault();
    const newPerson = {
      id: persons.length + 1,
      name: newName,
      number: number
    }
    const names = persons.map(person => person.name)
    names.includes(newName) ? alert(`${newName} is already added to phonebook`) : setPersons(persons.concat(newPerson));
    setFilteredPersons(persons.concat(newPerson))
    setNewName("");
    setNumber("");
  }

  const search = (event) => {
    const test = (persons.filter(person => person.name.toLowerCase().includes(event.target.value.toLowerCase())));
    setFilteredPersons(test);
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter search={search} />
      <h3>add a new</h3>
      <PersonForm addToPhoneBook={addToPhoneBook} newName={newName} handelNameChange={handelNameChange} number={number} handelNumberChange={handelNumberChange} />
      <h3>Numbers</h3>
      <Persons filteredPersons={filteredPersons} persons={persons} />
    </div>
  )
}

export default App
